mapper.select:
SELECT PDAU.BATCH_NUMBER, PDAU.COMPANY_ID, PDAU.STATUS, PDAU.CREATED_BY, PDAU.CREATION_DATE, PDAU.LAST_UPDATED_BY, PDAU.LAST_UPDATE_DATE, ( SELECT MIN(PDAE_1.UPLOAD_DATE) FROM PEA_DPA_AMOUNT_ELEMENTS PDAE_1 WHERE PDAE_1.BATCH_NUMBER = PDAU.BATCH_NUMBER ) AS PROCESS_DATE_FROM, ( SELECT MAX(PDAE_2.UPLOAD_DATE) FROM PEA_DPA_AMOUNT_ELEMENTS PDAE_2 WHERE PDAE_2.BATCH_NUMBER = PDAU.BATCH_NUMBER ) AS PROCESS_DATE_TO, SU.USER_NAME FROM PEA_DPA_AMOUNT_UPLOADS PDAU LEFT JOIN SAM_USERS SU ON(SU.USER_ID = PDAU.LAST_UPDATED_BY) WHERE PDAU.BATCH_NUMBER IN ( SELECT PDAE.BATCH_NUMBER FROM PEA_DPA_AMOUNT_ELEMENTS PDAE
AND PDAE.UPLOAD_DATE >= #{dateFrom, jdbcType=TIMESTAMP}
AND PDAE.UPLOAD_DATE <= #{dateTo, jdbcType=TIMESTAMP}
)
AND PDAU.BATCH_NUMBER >= #{batchNumberFrom}
AND PDAU.BATCH_NUMBER <= #{batchNumberTo}
AND PDAU.COMPANY_ID = #{companyId}

SELECT PDAU.BATCH_NUMBER, PDAU.COMPANY_ID, PDAU.STATUS, PDAU.CREATED_BY, PDAU.CREATION_DATE, PDAU.LAST_UPDATED_BY, PDAU.LAST_UPDATE_DATE, ( SELECT MIN(PDAE_1.UPLOAD_DATE) FROM PEA_DPA_AMOUNT_ELEMENTS PDAE_1 WHERE PDAE_1.BATCH_NUMBER = PDAU.BATCH_NUMBER ) AS PROCESS_DATE_FROM, ( SELECT MAX(PDAE_2.UPLOAD_DATE) FROM PEA_DPA_AMOUNT_ELEMENTS PDAE_2 WHERE PDAE_2.BATCH_NUMBER = PDAU.BATCH_NUMBER ) AS PROCESS_DATE_TO, SU.USER_NAME FROM PEA_DPA_AMOUNT_UPLOADS PDAU LEFT JOIN SAM_USERS SU ON(SU.USER_ID = PDAU.LAST_UPDATED_BY) WHERE PDAU.BATCH_NUMBER IN ( SELECT PDAE.BATCH_NUMBER FROM PEA_DPA_AMOUNT_ELEMENTS PDAE
AND PDAE.UPLOAD_DATE >= #{dateFrom, jdbcType=TIMESTAMP}
AND PDAE.UPLOAD_DATE <= #{dateTo, jdbcType=TIMESTAMP}
)
AND TO_NUMBER(PDAU.BATCH_NUMBER) >= #{batchNumberFrom}
AND TO_NUMBER(PDAU.BATCH_NUMBER) <= #{batchNumberTo}
AND PDAU.COMPANY_ID = #{companyId}
