mapper.select:

select 'false' as QUERYID,

from PEA_HOUSING_ALLOWANCE
where HOUSING_ALLOWANCE_ID = #{housingAllowanceId,jdbcType=DECIMAL}

select HOUSING_ALLOWANCE_ID , PERSON_ID , COMPANY_ID , EFFECTIVE_START_DATE , EFFECTIVE_END_DATE , HOUSE_ALLOWANCE , CREATED_BY , CREATION_DATE , LAST_UPDATED_BY , LAST_UPDATE_DATE , IS_LUMPSUM_REQUESTED from PEA_HOUSING_ALLOWANCE WHERE PERSON_ID = #{personId,jdbcType=DECIMAL}
and HOUSE_ALLOWANCE = #{houseAllowance,jdbcType=VARCHAR}
order by HOUSING_ALLOWANCE_ID desc
