) MGR JOIN PEA_PERSONAL_INFORMATIONS PPIS ON PPIS.PERSON_ID = MGR.PERSON_ID AND TRUNC(SYSDATE) BETWEEN PPIS.EFFECTIVE_START_DATE AND PPIS.EFFECTIVE_END_DATE AND MGR.COMPANY_ID = PPIS.COMPANY_ID JOIN hierarchy_structural HS ON HS.ORGANIZATION_ID = MGR.ORGANIZATION_ID LEFT JOIN ( SELECT WO.ORGANIZATION_ID FROM WOS_ORGANIZATIONS WO WHERE
wo.organization_id = #{item}
) MGR2 ON (HS.PATH LIKE '%/'||MGR2.ORGANIZATION_ID||'/%' OR HS.PATH LIKE '%/'||MGR2.ORGANIZATION_ID) AND MGR.ORGANIZATION_ID <> MGR2.ORGANIZATION_ID WHERE MGR2.ORGANIZATION_ID IS NULL AND HS.COMPANY_ID = #{companyId,jdbcType=NUMERIC} AND PPIS.PERSON_ID <> #{managerId} and
HS.BRANCH_ID = #{item}
AND PPIS.EMPLOYEE_NUMBER LIKE #{criteria.employeeNumber}
AND PPIS.FULL_NAME LIKE #{criteria.fullName}
)emp join CaM_APPRAISAL_DATA cad on emp.job_id = cad.JOB_ID and (cad.GRADE = emp.grade or cad.GRADE is null) and (cad.SUB_GRADE = emp.SUB_GRADE or cad.SUB_GRADE is null) ORDER BY TO_NUMBER(EMPLOYEE_NUMBER)

SELECT COUNT(1) FROM ( SELECT DISTINCT PPAS.ORGANIZATION_ID, PPIS.PERSON_ID, PPIS.EMPLOYEE_NUMBER, PPIS.FULL_NAME FROM PEA_PRIMARY_ASSIGNMENTS PPAS JOIN PEA_PERSONAL_INFORMATIONS PPIS ON PPIS.PERSON_ID = PPAS.PERSON_ID AND TRUNC(SYSDATE) BETWEEN PPIS.EFFECTIVE_START_DATE AND PPIS.EFFECTIVE_END_DATE AND PPAS.COMPANY_ID = PPIS.COMPANY_ID JOIN PEA_PEOPLE_TYPES PPT ON PPT.PERSON_ID = PPAS.PERSON_ID AND TRUNC(SYSDATE) BETWEEN PPT.EFFECTIVE_START_DATE AND PPT.EFFECTIVE_END_DATE WHERE PPAS.ORGANIZATION_ID IN ( SELECT HS.ORGANIZATION_ID FROM hierarchy_structural HS LEFT JOIN ( SELECT WO.ORGANIZATION_ID FROM WOS_ORGANIZATIONS WO WHERE
wo.organization_id = #{item}
) MGR ON (HS.PATH LIKE '%/'||MGR.ORGANIZATION_ID||'/%' OR HS.PATH LIKE '%/'||MGR.ORGANIZATION_ID) WHERE MGR.ORGANIZATION_ID IS NULL AND HS.COMPANY_ID = #{companyId,jdbcType=NUMERIC} and
hs.branch_id = #{item}
) AND TRUNC(SYSDATE) BETWEEN TRUNC(PPAS.EFFECTIVE_START_DATE) AND TRUNC(PPAS.EFFECTIVE_END_DATE) AND PPAS.PERSON_ID <> #{managerId} AND PPT.PEOPLE_TYPE = 'EMPLOYEE'
AND PPIS.EMPLOYEE_NUMBER LIKE #{criteria.employeeNumber}
AND PPIS.FULL_NAME LIKE #{criteria.fullName}
and PPAS.JOB_ID = #{criteria.jobId, jdbcType=NUMERIC}
UNION SELECT DISTINCT MGR.ORGANIZATION_ID, PPIS.PERSON_ID, PPIS.EMPLOYEE_NUMBER, PPIS.FULL_NAME FROM ( SELECT PPA.ORGANIZATION_ID, PPA.JOB_ID, PPA.COMPANY_ID, PPA.PERSON_ID FROM PEA_PRIMARY_ASSIGNMENTS PPA JOIN PEA_PEOPLE_TYPES PPT ON PPA.PERSON_ID= PPT.PERSON_ID AND PPT.PEOPLE_TYPE IN ('EMPLOYEE','INTERNSHIP') AND TRUNC(SYSDATE) BETWEEN TRUNC(PPT.EFFECTIVE_START_DATE) AND TRUNC(PPT.EFFECTIVE_END_DATE) JOIN WOS_JOB_VERSIONS WJV ON PPA.JOB_ID = WJV.JOB_ID AND TRUNC(SYSDATE) BETWEEN TRUNC(WJV.DATE_FROM) AND TRUNC(WJV.DATE_TO) AND IS_MANAGER = '1' JOIN WOS_ORGANIZATION_VERSIONS WOV ON WOV.HEAD_OF_ORGANIZATION = PPA.JOB_ID AND WOV.ORGANIZATION_ID = PPA.ORGANIZATION_ID AND TRUNC(SYSDATE) BETWEEN WOV.DATE_FROM AND WOV.DATE_TO WHERE TRUNC(SYSDATE) BETWEEN PPA.EFFECTIVE_START_DATE AND PPA.EFFECTIVE_END_DATE
and PPA.JOB_ID = #{criteria.jobId, jdbcType=NUMERIC}
and
ppa.organization_id = #{item}
) MGR JOIN PEA_PERSONAL_INFORMATIONS PPIS ON PPIS.PERSON_ID = MGR.PERSON_ID AND TRUNC(SYSDATE) BETWEEN PPIS.EFFECTIVE_START_DATE AND PPIS.EFFECTIVE_END_DATE AND MGR.COMPANY_ID = PPIS.COMPANY_ID JOIN hierarchy_structural HS ON HS.ORGANIZATION_ID = MGR.ORGANIZATION_ID LEFT JOIN ( SELECT WO.ORGANIZATION_ID FROM WOS_ORGANIZATIONS WO WHERE
wo.organization_id = #{item}
) MGR2 ON (HS.PATH LIKE '%/'||MGR2.ORGANIZATION_ID||'/%' OR HS.PATH LIKE '%/'||MGR2.ORGANIZATION_ID) AND MGR.ORGANIZATION_ID <> MGR2.ORGANIZATION_ID WHERE MGR2.ORGANIZATION_ID IS NULL AND HS.COMPANY_ID = #{companyId,jdbcType=NUMERIC} AND PPIS.PERSON_ID <> #{managerId} and
HS.BRANCH_ID = #{item}
AND PPIS.EMPLOYEE_NUMBER LIKE #{criteria.employeeNumber}
AND PPIS.FULL_NAME LIKE #{criteria.fullName}
)

select count(1) from pea_people p join pea_people_types t on p.person_id = t.person_id join pea_personal_informations i on i.person_id = p.person_id and i.company_id = t.company_id join pea_primary_assignments a on a.person_id = p.person_id and a.company_id = t.company_id left join bse_companies c on c.company_id = t.company_id left join wos_jobs jb on jb.job_id = a.job_id left join wos_job_versions jobv on jobv.job_id = jb.job_id left join wos_organizations org on org.organization_id = a.organization_id left join wos_organization_versions orgv on orgv.organization_id = org.organization_id left join wos_locations loc on loc.location_id = orgv.location_id left join wos_grades grade on grade.grade_id = a.grade_id left join wos_grade_versions gradev on gradev.grade_id = a.grade_id and #{criteria.effectiveOnDate, jdbcType=TIMESTAMP} between gradev.date_from and gradev.date_to left join BRANCH_HIERARCHY_V bh on bh.organization_id = a.organization_id left join wos_locations l on l.location_code = i.home_base_code and l.company_id = t.company_id left join pea_assignment_other_info paoi on paoi.assignment_id = a.assignment_id left join sam_users su on(su.user_id = p.last_updated_by)
and c.group_id = #{businessGroupId,jdbcType=NUMERIC}
and upper(c.company_name) like upper(#{criteria.companyName})

select distinct emp.* from (
and t.company_id = #{companyId,jdbcType=NUMERIC}
and i.employee_number in (SELECT ppi.employee_number FROM wos_organization_versions wov, pea_primary_assignments ppa, pea_personal_informations ppi, pea_people_types ppt WHERE wov.organization_id in (SELECT wohe.organization_id FROM wos_organization_hier woh, wos_org_hier_versions wohv, wos_org_hier_elements wohe, wos_organization_versions wov WHERE woh.org_hier_type = 'STRUCTURAL' AND woh.company_id = #{companyId,jdbcType=NUMERIC} AND wohv.org_hier_id = woh.org_hier_id AND TRUNC (SYSDATE) BETWEEN wohv.date_from AND wohv.date_to AND wohe.version_id = wohv.version_id and TRUNC (SYSDATE) BETWEEN wov.date_from AND wov.date_to and wohe.organization_id = wov.organization_id and wov.organization_spv_id is null AND wohe.parent_id = #{orgParent}) AND TRUNC (SYSDATE) BETWEEN wov.date_from AND wov.date_to AND ppa.organization_id = wov.organization_id AND TRUNC (SYSDATE) BETWEEN ppa.effective_start_date AND ppa.effective_end_date AND ppi.person_id = ppa.person_id AND ppi.company_id = ppa.company_id AND TRUNC (SYSDATE) BETWEEN ppi.effective_start_date AND ppi.effective_end_date AND ppt.person_id = ppi.person_id AND ppt.company_id = ppi.company_id AND TRUNC (SYSDATE) BETWEEN ppt.effective_start_date AND ppt.effective_end_date AND ppt.people_type = 'EMPLOYEE'
union
SELECT ppi.employee_number FROM wos_organization_versions wov, pea_primary_assignments ppa, pea_personal_informations ppi, pea_people_types ppt WHERE wov.organization_id in (SELECT organization_id FROM wos_organization_versions WHERE TRUNC (SYSDATE) BETWEEN date_from AND date_to AND organization_spv_id = #{orgParent}) AND TRUNC (SYSDATE) BETWEEN wov.date_from AND wov.date_to AND ppa.organization_id = wov.organization_id AND TRUNC (SYSDATE) BETWEEN ppa.effective_start_date AND ppa.effective_end_date AND ppi.person_id = ppa.person_id AND ppi.company_id = ppa.company_id AND TRUNC (SYSDATE) BETWEEN ppi.effective_start_date AND ppi.effective_end_date AND ppt.person_id = ppi.person_id AND ppt.company_id = ppi.company_id AND TRUNC (SYSDATE) BETWEEN ppt.effective_start_date AND ppt.effective_end_date AND ppt.people_type = 'EMPLOYEE'
UNION SELECT ppi.employee_number FROM wos_organization_versions wov, pea_primary_assignments ppa, pea_personal_informations ppi, PEA_PEOPLE_TYPES PPT WHERE wov.organization_id = #{orgParent} AND TRUNC (SYSDATE) BETWEEN wov.date_from AND wov.date_to AND ppa.organization_id = wov.organization_id AND TRUNC (SYSDATE) BETWEEN ppa.effective_start_date AND ppa.effective_end_date AND ppi.person_id = ppa.person_id AND ppi.company_id = ppa.company_id AND TRUNC (SYSDATE) BETWEEN ppi.effective_start_date AND ppi.effective_end_date AND ppt.person_id = ppi.person_id AND ppt.company_id = ppi.company_id AND TRUNC (SYSDATE) BETWEEN PPT.EFFECTIVE_START_DATE AND PPT.EFFECTIVE_END_DATE AND PPT.PEOPLE_TYPE = 'EMPLOYEE' and ppi.person_id <> #{criteria.supervisorId}
)
ORDER BY to_number(i.employee_number) ASC ) emp join CaM_APPRAISAL_DATA cad on emp.job_id = cad.JOB_ID and (cad.GRADE = emp.grade or cad.GRADE is null) and (cad.SUB_GRADE = emp.SUB_GRADE or cad.SUB_GRADE is null)
