mapper.select:

select 'false' as QUERYID,

from SOC_ASSET_LABELING_DTL
where LABELING_DTL_ID = #{labelingDetailId,jdbcType=DECIMAL}

mapper.insert:


  select SOC_ASSET_LABELING_DTL_S.nextVal from dual

insert into SOC_ASSET_LABELING_DTL (LABELING_DTL_ID, LABELING_ID, ASSET_ID, REPRINTING_REASON_ID,REPRINTING_REASON_CODE, CREATED_BY, CREATION_DATE, LAST_UPDATE_BY, LAST_UPDATE_DATE
  )
values (#{labelingDetailId,jdbcType=DECIMAL}, #{labelingId,jdbcType=DECIMAL}, #{assetId,jdbcType=DECIMAL},
  (select lookup_id from BSE_LOOKUP_DEPENDENTS where detail_code= #{reprintingReasonCode,jdbcType=VARCHAR}),#{reprintingReasonCode,jdbcType=VARCHAR}, #{createdBy,jdbcType=DECIMAL}, 
  #{creationDate,jdbcType=TIMESTAMP}, #{lastUpdatedBy,jdbcType=DECIMAL}, #{lastUpdateDate,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT DISTINCT A.ASSET_ID, A.ASSET_NUMBER, (BSE.DESCRIPTION) as LOOKUP_DESC, A.BOOK_TYPE_CODE, A.SERIAL_NUMBER, A.DESCRIPTION, A.CATEGORY_ID, A.COMPANY_ID, A.BRANCH_ID, (select description from bse_lookup_dependents where detail_code= A.ASSET_TYPE_CODE) as ASSET_TYPE_NAME, (select segment1 from SOC_EBS_CATEGORIES where category_id =A.CATEGORY_ID) as CATEGORY_NAME, (case when A.BRANCH_ID = -1 then 'HEADOFFICE' else (SELECT SUBSTR (ORGANIZATION_CODE, -5) as branch_code from WOS_ORGANIZATIONS where ORGANIZATION_ID = A.BRANCH_ID and rownum = 1) end )BRANCH_CODE, (case when A.BRANCH_ID = -1 then 'Head Office' else (SELECT ORGANIZATION_NAME from WOS_ORGANIZATIONS where ORGANIZATION_ID = A.BRANCH_ID and rownum = 1) end )BRANCH_NAME, (SELECT LOCATION_CODE from WOS_LOCATIONS where LOCATION_ID = A.LOCATION_ID and rownum =1) LOCATION_CODE, (SELECT LOCATION_NAME from WOS_LOCATIONS where LOCATION_ID = A.LOCATION_ID and rownum =1) LOCATION_NAME, A.DATE_PLACED_IN_SERVICE, A.LIFE_IN_MONTHS, A.ORIGINAL_COST, A.UNREVALUED_COST, A.LAST_PRINTED_DATE, A.PRINTED_COUNT, A.CREATED_BY, A.CREATION_DATE, A.LAST_UPDATE_BY, A.LAST_UPDATE_DATE FROM SOC_ASSETS A INNER JOIN SOC_ASSET_LABELING_DTL LD on LD.ASSET_ID = A.ASSET_ID INNER JOIN SOC_ASSET_LABELING_HDR LH on LH.LABELING_ID = LD.LABELING_ID LEFT JOIN BSE_LOOKUP_DEPENDENTS BSE on BSE.DETAIL_CODE = LD.REPRINTING_REASON_CODE
LH.LABELING_ID = #{example.labelingId,jdbcType=DECIMAL}
ORDER BY A.LAST_UPDATE_DATE ASC

mapper.update:

update SOC_ASSETS
set 
  	PRINTED_COUNT = #{printedCount,jdbcType=DECIMAL},
 	LAST_PRINTED_DATE = #{lastPrintedDate,jdbcType=DATE}
where 
	ASSET_ID = #{assetId,jdbcType=DECIMAL}
