mapper.select:

select distinct

from SOC_ASSETS
where BRANCH_ID = #{branchId,jdbcType=DECIMAL}


		select distinct

		from SOC_STOCK_OPNAME_UNREG_ASSETS so
			inner join soc_ebs_categories ec on ec.category_id = so.category_id
        	LEFT JOIN SAM_USERS U ON U.USER_ID = SO.LAST_UPDATE_BY
        	inner join soc_registration_dtl rd on rd.stock_opname_unreg_asset_id = so.stock_opname_unreg_asset_id
        	inner join soc_registration_hdr rh on rh.registration_id = rd.registration_id
		where so.BRANCH_ID = #{branchId,jdbcType=DECIMAL}
        	and so.COMPANY_ID = #{companyId,jdbcType=DECIMAL}
        	and rh.registration_id=#{registrationId,jdbcType=DECIMAL}
