mapper.select:
SELECT DISTINCT T.TRANSFER_ID, T.AVM_TRX_ID, T.COMPANY_ID, T.BRANCH_ID, T.REQUEST_NO, T.REQUEST_DATE, T.BRANCH_ORIGIN, T.BRANCH_DESTINATION, (case when T.BRANCH_ORIGIN = -1 then 'HEADOFFICE' else (SELECT SUBSTR (ORGANIZATION_CODE, -5) as branch_code from WOS_ORGANIZATIONS where ORGANIZATION_ID = T.BRANCH_ORIGIN and rownum = 1) end )BRANCH_CODE_ORIGIN, (case when T.BRANCH_ORIGIN = -1 then 'Head Office' else (SELECT ORGANIZATION_NAME from WOS_ORGANIZATIONS where ORGANIZATION_ID = T.BRANCH_ORIGIN and rownum = 1) end )BRANCH_NAME_ORIGIN, (case when T.BRANCH_DESTINATION = -1 then 'HEADOFFICE' else (SELECT SUBSTR (ORGANIZATION_CODE, -5) as branch_code from WOS_ORGANIZATIONS where ORGANIZATION_ID = T.BRANCH_DESTINATION and rownum = 1) end )BRANCH_CODE_DESTINATION, (case when T.BRANCH_DESTINATION = -1 then 'Head Office' else (SELECT ORGANIZATION_NAME from WOS_ORGANIZATIONS where ORGANIZATION_ID = T.BRANCH_DESTINATION and rownum = 1) end )BRANCH_NAME_DESTINATION, (case when T.REASON is null then '-' else T.REASON end )REASON, T.STATUS_ID, T.STATUS_CODE, LD.DESCRIPTION, T.CREATED_BY, T.CREATION_DATE, T.LAST_UPDATE_BY, T.LAST_UPDATE_DATE, (SELECT CONCAT(concat(PEA.EMPLOYEE_NUMBER,'-'),PEA.FULL_NAME) FROM pea_personal_informations PEA WHERE ROWNUM = 1 AND PEA.EMPLOYEE_NUMBER = U.USER_NAME) AS FULL_NAME, U.USER_NAME from SOC_TRANSFER_HDR T INNER JOIN BSE_LOOKUP_DEPENDENTS LD on LD.DETAIL_CODE = T.STATUS_CODE LEFT JOIN SAM_USERS U ON U.USER_ID = T.LAST_UPDATE_BY INNER JOIN PEA_PERSONAL_INFORMATIONS PEA ON PEA.PERSON_ID = U.PERSON_ID
and EBS_FLAG= 'Y'
order by ${orderByClause}
