mapper.select:

select 'false' as QUERYID,

from SOC_RETIREMENT_HDR HD
where RETIREMENT_ID = #{id,jdbcType=DECIMAL}

mapper.update:

update SOC_RETIREMENT_HDR
  set 
  RV_NUMBER = #{rvNumber,jdbcType=VARCHAR}
where RETIREMENT_ID = #{id,jdbcType=DECIMAL}

mapper.select:
SELECT bld.LOOKUP_ID KEY, bld.DETAIL_CODE VALUE, bld.DESCRIPTION DESCRIPTION FROM bse_lookup_hdr blh join BSE_LOOKUP_DEPENDENTS bld on bld.lookup_id = blh.lookup_id WHERE blh.name = #{lookupName}
AND UPPER(bld.DETAIL_CODE) = UPPER(#{key})
ORDER BY bld.DESCRIPTION ASC
